function KurseDialog({
  open,
  onOpenChange,
  record,
  onSuccess,
  raeume,
  dozenten,
}: {
  open: boolean;
  onOpenChange: (v: boolean) => void;
  record: Kurse | null;
  onSuccess: () => void;
