  }

  return (
    <Dialog open={open} onOpenChange={onOpenChange}>
      <DialogContent className="sm:max-w-md">
        <DialogHeader>
          <DialogTitle>{isEdit ? 'Anmeldung bearbeiten' : 'Neue Anmeldung'}</DialogTitle>
