  --chart-2: oklch(0.65 0.12 185);   /* teal */
  --chart-3: oklch(0.55 0.18 270);   /* indigo */
  --chart-4: oklch(0.65 0.20 15);    /* rose */
  --chart-5: oklch(0.65 0.16 155);   /* emerald */

  /* Sidebar */
  --sidebar: oklch(0.16 0.04 255);
  --sidebar-foreground: oklch(0.80 0.02 250);
  --sidebar-primary: oklch(0.75 0.175 70);
  --sidebar-primary-foreground: oklch(0.15 0.04 255);
