  /* Custom tokens */
  --shadow-card: 0 1px 3px oklch(0.16 0.03 255 / 0.08), 0 1px 2px oklch(0.16 0.03 255 / 0.04);
  --shadow-elevated: 0 4px 12px oklch(0.16 0.03 255 / 0.12), 0 2px 4px oklch(0.16 0.03 255 / 0.06);
  --shadow-modal: 0 20px 60px oklch(0.16 0.03 255 / 0.2);
  --transition: all 0.2s ease;
}

@layer base {
  * {
    @apply border-border outline-ring/50;
