            <div className="space-y-1.5">
              <Label>Telefon</Label>
              <Input value={form.telefon ?? ''} onChange={(e) => setForm((f) => ({ ...f, telefon: e.target.value }))} placeholder="+49 123 456789" />
            </div>
            <div className="space-y-1.5">
