              value={formData.kapazitaet}
              onChange={(e) => setFormData((p) => ({ ...p, kapazitaet: e.target.value }))}
            />
          </div>
          <DialogFooter>
            <Button type="button" variant="outline" onClick={() => onOpenChange(false)}>
