                onChange={(e) => setFormData((p) => ({ ...p, preis: e.target.value }))}
              />
            </div>
          </div>
          <div className="space-y-2">
            <Label>Dozent</Label>
            <Select
              value={formData.dozent}
              onValueChange={(v) => setFormData((p) => ({ ...p, dozent: v }))}
            >
