                <SelectValue placeholder="Dozent waehlen..." />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="none">Kein Dozent</SelectItem>
                {dozenten.map((d) => (
                  <SelectItem key={d.record_id} value={d.record_id}>
                    {d.fields.dozent_firstname} {d.fields.dozent_lastname}
