            </Button>
            <Button type="submit" disabled={loading}>
              {loading ? "Speichern..." : participant ? "Aktualisieren" : "Erstellen"}
            </Button>
          </DialogFooter>
        </form>
      </DialogContent>
    </Dialog>
  );
}

