        email: participant.fields.email || "",
        telefon: participant.fields.telefon || "",
        geburtsdatum: participant.fields.geburtsdatum || "",
      });
    } else {
      setFormData({
        teilnehmer_firstname: "",
        teilnehmer_lastname: "",
        email: "",
        telefon: "",
        geburtsdatum: "",
      });
