@import "tailwindcss";
@import "tw-animate-css";
@import url('https://fonts.googleapis.com/css2?family=Plus+Jakarta+Sans:wght@300;400;500;600;700;800&display=swap');

@custom-variant dark (&:is(.dark *));

@theme inline {
  --radius-sm: calc(var(--radius) - 4px);
  --radius-md: calc(var(--radius) - 2px);
  --radius-lg: var(--radius);
  --radius-xl: calc(var(--radius) + 4px);
  --color-background: var(--background);
  --color-foreground: var(--foreground);
  --color-card: var(--card);
  --color-card-foreground: var(--card-foreground);
  --color-popover: var(--popover);
  --color-popover-foreground: var(--popover-foreground);
  --color-primary: var(--primary);
  --color-primary-foreground: var(--primary-foreground);
  --color-secondary: var(--secondary);
  --color-secondary-foreground: var(--secondary-foreground);
  --color-muted: var(--muted);
  --color-muted-foreground: var(--muted-foreground);
  --color-accent: var(--accent);
  --color-accent-foreground: var(--accent-foreground);
  --color-destructive: var(--destructive);
  --color-border: var(--border);
  --color-input: var(--input);
  --color-ring: var(--ring);
  --color-chart-1: var(--chart-1);
  --color-chart-2: var(--chart-2);
  --color-chart-3: var(--chart-3);
  --color-chart-4: var(--chart-4);
  --color-chart-5: var(--chart-5);
  --color-sidebar: var(--sidebar);
  --color-sidebar-foreground: var(--sidebar-foreground);
  --color-sidebar-primary: var(--sidebar-primary);
  --color-sidebar-primary-foreground: var(--sidebar-primary-foreground);
  --color-sidebar-accent: var(--sidebar-accent);
  --color-sidebar-accent-foreground: var(--sidebar-accent-foreground);
  --color-sidebar-border: var(--sidebar-border);
  --color-sidebar-ring: var(--sidebar-ring);
  --font-sans: 'Plus Jakarta Sans', system-ui, sans-serif;
}

:root {
  --radius: 0.75rem;

  /* Base colors - Cool off-white with navy */
  --background: hsl(222 47% 97%);
  --foreground: hsl(222 47% 11%);

  /* Primary - Warm Amber */
  --primary: hsl(35 95% 55%);
  --primary-foreground: hsl(222 47% 11%);
  --primary-glow: hsl(35 95% 70%);

  /* Secondary */
  --secondary: hsl(222 30% 93%);
  --secondary-foreground: hsl(222 47% 20%);

  /* Muted */
  --muted: hsl(222 20% 92%);
  --muted-foreground: hsl(222 15% 45%);

  /* Accent - Teal */
  --accent: hsl(162 73% 46%);
  --accent-foreground: hsl(162 80% 15%);

  /* Cards */
  --card: hsl(0 0% 100%);
  --card-foreground: hsl(222 47% 11%);

  /* Popover */
  --popover: hsl(0 0% 100%);
  --popover-foreground: hsl(222 47% 11%);

  /* Destructive */
  --destructive: hsl(0 84% 60%);

  /* Borders & Input */
  --border: hsl(222 20% 88%);
  --input: hsl(222 20% 88%);
  --ring: hsl(35 95% 55%);

  /* Charts */
  --chart-1: hsl(35 95% 55%);
  --chart-2: hsl(162 73% 46%);
  --chart-3: hsl(222 47% 45%);
  --chart-4: hsl(280 60% 55%);
  --chart-5: hsl(195 75% 50%);

  /* Sidebar */
  --sidebar: hsl(222 47% 15%);
  --sidebar-foreground: hsl(222 20% 90%);
  --sidebar-primary: hsl(35 95% 55%);
  --sidebar-primary-foreground: hsl(222 47% 11%);
  --sidebar-accent: hsl(222 47% 22%);
  --sidebar-accent-foreground: hsl(222 20% 90%);
  --sidebar-border: hsl(222 47% 25%);
  --sidebar-ring: hsl(35 95% 55%);

  /* Custom design tokens */
  --gradient-hero: linear-gradient(135deg, hsl(222 47% 15%) 0%, hsl(222 47% 25%) 100%);
  --gradient-card-hover: linear-gradient(180deg, hsl(0 0% 100%) 0%, hsl(222 30% 98%) 100%);
  --gradient-primary: linear-gradient(135deg, hsl(35 95% 55%) 0%, hsl(35 95% 65%) 100%);

  --shadow-sm: 0 2px 8px hsl(222 47% 11% / 0.04);
  --shadow-md: 0 4px 20px hsl(222 47% 11% / 0.08);
  --shadow-lg: 0 8px 40px hsl(222 47% 11% / 0.12);
  --shadow-glow: 0 0 30px hsl(35 95% 55% / 0.3);
}

.dark {
  --background: hsl(222 47% 8%);
  --foreground: hsl(222 20% 95%);
  --card: hsl(222 47% 12%);
  --card-foreground: hsl(222 20% 95%);
  --popover: hsl(222 47% 12%);
  --popover-foreground: hsl(222 20% 95%);
  --primary: hsl(35 95% 55%);
  --primary-foreground: hsl(222 47% 11%);
  --secondary: hsl(222 30% 18%);
  --secondary-foreground: hsl(222 20% 90%);
  --muted: hsl(222 20% 20%);
  --muted-foreground: hsl(222 15% 60%);
  --accent: hsl(162 73% 46%);
  --accent-foreground: hsl(162 80% 95%);
  --destructive: hsl(0 84% 60%);
  --border: hsl(222 20% 20%);
  --input: hsl(222 20% 20%);
  --ring: hsl(35 95% 55%);
}

@layer base {
  * {
    @apply border-border outline-ring/50;
  }
  body {
    @apply bg-background text-foreground font-sans antialiased;
  }
}

/* Custom utility classes */
@layer utilities {
  .gradient-hero {
    background: var(--gradient-hero);
  }
  .gradient-primary {
    background: var(--gradient-primary);
  }
  .shadow-card {
    box-shadow: var(--shadow-md);
  }
  .shadow-card-hover {
    box-shadow: var(--shadow-lg);
  }
  .shadow-glow {
    box-shadow: var(--shadow-glow);
  }
  .text-balance {
    text-wrap: balance;
  }
}

/* Progress bar custom styling */
.progress-fill {
  background: var(--gradient-primary);
  transition: width 0.5s cubic-bezier(0.4, 0, 0.2, 1);
}

/* Card hover effect */
.card-hover {
  transition: all 0.2s ease-out;
}
.card-hover:hover {
  transform: translateY(-2px);
  box-shadow: var(--shadow-lg);
}

/* Scrollbar styling */
::-webkit-scrollbar {
  width: 8px;
  height: 8px;
}
::-webkit-scrollbar-track {
  background: var(--muted);
  border-radius: 4px;
}
::-webkit-scrollbar-thumb {
  background: hsl(222 20% 70%);
  border-radius: 4px;
}
::-webkit-scrollbar-thumb:hover {
  background: hsl(222 20% 60%);
}
